import React from "react";
import SubjectCard from "./SubjectCard";
import StatBlock from "./StatBlock";
import ActionBlock from "./ActionBlock";

class Grid extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            subject: {
                name: "lich",
                description: "A lich often haunts the abode it favored in life, such as a lonely tower, a haunted ruin, or an " +
                    "academy of black magic. Alternatively, some liches construct secret tombs filled with powerful guardians " +
                    "and traps.",
                type: "undead",
                size: "medium",
                alignment: "any evil alignment",
                baseStats: {
                    ac: 135,
                    hp: 17,
                    speed: "30 ft"
                },
                attributes: {
                    strength: 11,
                    dexterity: 16,
                    constitution: 16,
                    intelligence: 20,
                    wisdom: 14,
                    charisma: 16,
                },
                saves: {
                    constitution: 10,
                    intelligence: 12,
                    wisdom: 9
                },
            },
        };

        this.base = Object.keys(this.state.subject.saves).map((saves, title) =>
            <p className="base-stats-saves">{title} {this.state.subject.saves[saves]}</p>
        );
    }


    render (){
        return (
            <div className={"grid"}>
                <SubjectCard
                    subject={this.state.subject.name}
                    subjectBody={this.state.subject.description}
                />
                <StatBlock subject={this.state.subject} baseStats={this.base}/>
                <ActionBlock/>
            </div>
        );
    }
}

export default Grid;
