import React from "react";
import SubjectCard from "./SubjectCard";
import StatBlock from "./StatBlock";
import ActionBlock from "./ActionBlock";

class Grid extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            subject: {
                name: "lich",
                description: "A lich often haunts the abode it favored in life, such as a lonely tower, a haunted ruin, or an " +
                    "academy of black magic. Alternatively, some liches construct secret tombs filled with powerful guardians " +
                    "and traps.",
                type: "undead",
                size: "medium",
                alignment: "any evil alignment",
                baseStats: {
                    hp: 135,
                    ac: 17,
                    speed: "30 ft",
                },
                attributes: {
                    strength: 11,
                    dexterity: 16,
                    constitution: 16,
                    intelligence: 20,
                    wisdom: 14,
                    charisma: 16,
                },
                saves: {
                    constitutionSave: 10,
                    intelligenceSave: 12,
                    wisdomSave: 9
                }
            },
        };

        const baseStats = this.baseStats.map((stat) => {
            <p className={"base-stats-throws"}></p>
        })
    }

    render (){
        return (
            <div className={"grid"}>
                <SubjectCard
                    subject={this.state.subject.name}
                    subjectBody={this.state.subject.description}
                />
                <StatBlock subject={this.state.subject}/>
                <ActionBlock/>
            </div>
        );
    }
}

export default Grid;
