import React from "react";
import SubjectCard from "./SubjectCard";
import StatBlock from "./StatBlock";
import ActionBlock from "./ActionBlock";

class Grid extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            subject: "lich",
            subjectBody: "A lich often haunts the abode it favored in life, such as a lonely tower, a haunted ruin, or an " +
                "academy of black magic. Alternatively, some liches construct secret tombs filled with powerful guardians " +
                "and traps."
        };
    }

    render (){
        return (
            <div className={"grid"}>
                <SubjectCard
                    subject={this.props.subject}
                    subjectBody={"A lich often haunts the abode it favored in life, such as a lonely tower, " +
                    "a haunted ruin, or an academy of black magic. Alternatively, some liches construct secret " +
                    "tombs filled with powerful guardians and traps."}
                />
                <StatBlock/>
                <ActionBlock/>
            </div>
        );
    }
}

export default Grid;
